import  AsyncStorage from "@react-native-async-storage/async-storage"

export const getFromAsyncStorage = async (key: string, setEmail: any) => {
   return AsyncStorage.getItem(key)
    .then((value)=> setEmail(value))
    .catch((error)=> console.log(error))
}

export const findPasswordAsync = (setPassword: any) => {
    AsyncStorage.getItem('password')
    .then((value)=> setPassword(value))
    .catch((error)=> console.log(error))
}

export const storeInAsyncStorage = (key: string, value: any) => {
    AsyncStorage.setItem(key, value)
}
